package com.basics.java;

public class swapnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x =10;
		int y= 19;
		
		System.out.println("Beefore Swapping : X=" + x  +" Y= "+y);
		
		x = x+y;
		y=x-y;
		x=x-y;
		System.out.println("After Swapping : X=" + x  +" Y= "+y);
				

	}

}
